class Player:
    def __init__(self, id, name, age, height, weight, shot, birth_place, birthdate, number):
        self.id = id
        self.name = name
        self.age = age
        self.height = height
        self.weight = weight
        self.shot = shot
        self.birth_place = birth_place
        self.birthdate = birthdate
        self.number = number